    private void addRadiologyofPatients() {
        DBAccess d = new DBAccess();
        ArrayList<Radiology> rad = d.retrieveRadiologybyMRN(doctor_homepage.getPatientMRN());
        DefaultTableModel model = (DefaultTableModel) radio.getModel();
        model.setRowCount(0);

        if (rad.size()>0){
            for (Radiology r : rad) {
                Object[] rowData = {
                    r.getRadiologyId(),
                    r.getRadiologyName(),
                    r.getReport(),
                    r.getReason()
                };
                model.addRow(rowData);
            }
        }
    }

